class Calculator{constructor(t){this.formData=t}getPerPersonAmount(){if(!this.formData.accountAmount||!this.formData.amountOfPeople)return;const t=parseInt(this.formData.amountOfPeople),a=this.getTotalAccount()/t;return a}getTotalAccount(){if(!this.formData.accountAmount||!this.formData.percentage)return;const t=Number(this.formData.accountAmount.replace(/[.,]/g,"")),a=Number(this.formData.percentage),o=Number(this.formData.round),r=t/100*a;let e=r+t;switch(o){case 2:e=Math.ceil(e);break;case 3:e=Math.floor(e)}return e}}export{Calculator};